mapper.insert:
INSERT INTO board
    SET categoryId = #{categoryId}
    , memberId = #{memberId}
    , title = #{title}
    , content = #{content}
    , regDate = NOW()
    , updateDate = NOW()

mapper.update:
UPDATE board
    SET categoryId = #{categoryId}
    , title = #{title}
    , content = #{content}
    , updateDate = NOW()
    WHERE boardId = #{boardId}

UPDATE board
    SET hit = hit + 1
        WHERE boardId = #{boardId}
